<script>
  import { onMount } from "svelte";
  import { time } from "../../stores.js";

  $: apm = $time.h > 11 ? "PM" : "AM";

  const C_W = 1000,
    C_H = 1000;

  let canvas;
  onMount(() => {
    const ctx = canvas.getContext("2d");

    function makeHand(w, l, color = "black", offset = 30) {
      // hand
      ctx.strokeStyle = color;
      ctx.beginPath();
      ctx.moveTo(0, offset);
      ctx.lineWidth = w;
      ctx.lineTo(0, -l);
      ctx.stroke();

      // center dot
      ctx.beginPath();
      ctx.arc(0, 0, 4 + w * 0.6, 0, Math.PI * 2, true); // Outer circle
      ctx.fillStyle = color;
      ctx.fill();
    }

    function draw() {
      ctx.lineCap = "round";

      ctx.save();
      ctx.clearRect(0, 0, C_W, C_H); // clear canvas
      ctx.translate(C_W / 2, C_H / 2); // center orgin

      // HOUR HAND
      ctx.save();
      ctx.rotate(
        $time.h * (Math.PI / 6) +
          (Math.PI / 360) * $time.m +
          (Math.PI / 21600) * $time.s
      );
      makeHand(20, 300, "#ccc", 0);
      ctx.restore();

      // MIN HAND
      ctx.save();
      ctx.rotate((Math.PI / 30) * $time.m + (Math.PI / 1800) * $time.s);
      makeHand(10, 390, "white", 0);
      ctx.restore();

      // SECOND HAND
      ctx.save();
      ctx.rotate(($time.s * Math.PI) / 30);
      makeHand(3, 420, "red");
      ctx.restore();

      ctx.restore();
      window.requestAnimationFrame(draw);
    }

    window.requestAnimationFrame(draw);
  });
</script>

